%spark2

import org.apache.spark.sql.types._
import org.apache.spark.sql.DataFrame
import scala.collection.mutable.ListBuffer


//Note the location of the Data File - hdfs://HostWithNameNode:8020/tmp/MushroomDatabase.csv
//Create a data frame from CSV File 
val df_WholeSetRaw = sqlContext.read.format("com.databricks.spark.csv").option("header", "true").option("inferSchema", "true").load("hdfs://publicsectordemo-1.field.hortonworks.com:8020/tmp/cfs_2012_pumf.csv")

//Create Table from DataFrame
df_WholeSetRaw.createOrReplaceTempView("CensusECON")

//Display resulting Infered schema 
df_WholeSetRaw.printSchema()


//Load Helper Files and Build Tables

//CFS Area
val df_Table_CFSArea = sqlContext.read.format("com.databricks.spark.csv").option("header", "true").option("inferSchema", "true").load("hdfs://publicsectordemo-1.field.hortonworks.com:8020/tmp/CFS_2012_table_CFSArea.csv")

//Mode of Transportation 
val df_Table_ModeofTrans = sqlContext.read.format("com.databricks.spark.csv").option("header", "true").option("inferSchema", "true").load("hdfs://publicsectordemo-1.field.hortonworks.com:8020/tmp/CFS_2012_table_ModeofTrans.csv")

//NAICS - North American Industry Classificatio System Codes
val df_Table_NAICS = sqlContext.read.format("com.databricks.spark.csv").option("header", "true").option("inferSchema", "true").load("hdfs://publicsectordemo-1.field.hortonworks.com:8020/tmp/CFS_2012_table_NAICS.csv")

//SCTG - Standard Classification of Transported Goods Codes
val df_Table_SCTG = sqlContext.read.format("com.databricks.spark.csv").option("header", "true").option("inferSchema", "true").load("hdfs://publicsectordemo-1.field.hortonworks.com:8020/tmp/CFS_2012_table_SCTG.csv")

//State Codes
val df_Table_StateCodes = sqlContext.read.format("com.databricks.spark.csv").option("header", "true").option("inferSchema", "true").load("hdfs://publicsectordemo-1.field.hortonworks.com:8020/tmp/CFS_2012_table_StateCodes.csv")

//Create Table from DataFrames
df_Table_CFSArea.createOrReplaceTempView("CensusECON_CFSArea")
df_Table_ModeofTrans.createOrReplaceTempView("CensusECON_MoT")
df_Table_NAICS.createOrReplaceTempView("CensusECON_NAICS")
df_Table_SCTG.createOrReplaceTempView("CensusECON_SCTG")
df_Table_StateCodes.createOrReplaceTempView("CensusECON_StateCodes")
